#!/bin/bash
# Script for running nips gridworld experiments with VIN networks
mkdir -p results
# 8x8 map
THEANO_FLAGS='floatX=float32,device=gpu' python NN_run_training.py --input ./data/gridworld_8.mat --output ./results/grid8_VIN.pk --epochs 30 --model valIterBatch --stepsize 0.01 --imsize 8 --reg 0.0 --k 10 --batchsize 12 --statebatchsize 10 | tee ./results/out_grid8_VIN.txt ;
THEANO_FLAGS='floatX=float32,device=gpu' python NN_run_training.py --input ./data/gridworld_8.mat --output ./results/grid8_VIN.pk --epochs 30 --model valIterBatch --stepsize 0.005 --imsize 8 --reg 0.0 --k 10 --batchsize 12 --statebatchsize 10 --warmstart ./results/grid8_VIN.pk | tee -a ./results/out_grid8_VIN.txt ;
THEANO_FLAGS='floatX=float32,device=gpu' python NN_run_training.py --input ./data/gridworld_8.mat --output ./results/grid8_VIN.pk --epochs 30 --model valIterBatch --stepsize 0.002 --imsize 8 --reg 0.0 --k 10 --batchsize 12 --statebatchsize 10 --warmstart ./results/grid8_VIN.pk | tee -a ./results/out_grid8_VIN.txt ;
